    <script>
        // Script untuk menampilkan/menyembunyikan animatedRectangle saat tombol showRectangle diklik
        document.getElementById('showRectangle').addEventListener('click', function() {
            var container = document.getElementById('animatedRectangle');
            var sensorItems = document.getElementById('sensorItems');
            var sensorMenuItem = document.getElementById('menu-sensor');
    
            container.style.display = (container.style.display === 'none') ? 'block' : 'none';
            // Menyembunyikan sensorItems saat animatedRectangle ditampilkan
            if (container.style.display === 'block') {
                sensorItems.style.display = 'none';
                sensorMenuItem.classList.remove('active-sensor-item');
            }
        });
    
        function setActiveMenuItem(event, menuId) {
            event.preventDefault();
            document.querySelectorAll('.menu-item, .menu-user').forEach(item => item.classList.remove('active'));
            document.getElementById(`menu-${menuId}`).classList.add('active');
            if (event.currentTarget.href) {
                window.location.href = event.currentTarget.href;
            }
        }
    
        function handleLogoutClick(event) {
            event.preventDefault();
            // Add clicked class to the menu-logout item
            document.querySelector('.menu-logout').classList.add('clicked');
            // Submit the logout form
            document.getElementById('logout-form').submit();
        }
    
        // Function to toggle sensor items
        function toggleSensorItems(event) {
            event.preventDefault();
            const sensorItems = document.getElementById('sensorItems');
            const sensorMenuItem = document.getElementById('menu-sensor');
            const animatedRectangle = document.getElementById('animatedRectangle');
            const isVisible = sensorItems.style.display === 'block';
            sensorItems.style.display = isVisible ? 'none' : 'block';
            // Add or remove the active-sensor-item class based on visibility
            sensorMenuItem.classList.toggle('active-sensor-item', !isVisible);
            // Menyembunyikan animatedRectangle saat sensorItems ditampilkan
            if (sensorItems.style.display === 'block') {
                animatedRectangle.style.display = 'none';
            }
        }

        document.addEventListener('DOMContentLoaded', () => {
            const currentUrl = window.location.pathname;
            document.querySelectorAll('.menu-item a, .menu-user a').forEach(link => {
                if (link.href.includes(currentUrl)) {
                    link.parentElement.classList.add('active');
                }
            });
        })
    
        document.addEventListener('DOMContentLoaded', function () {
            const toggleButton = document.querySelector('.toggle-button');
            const sidebar = document.querySelector('.sidebar');
            const overlay = document.querySelector('.overlay');
            const closeSidebarButton = document.querySelector('.close-sidebar-button');
    
            toggleButton.addEventListener('click', function () {
                sidebar.classList.toggle('active');
                overlay.classList.toggle('active');
            });
    
            closeSidebarButton.addEventListener('click', function () {
                sidebar.classList.remove('active');
                overlay.classList.remove('active');
            });
    
            overlay.addEventListener('click', function () {
                sidebar.classList.remove('active');
                overlay.classList.remove('active');
            });
        });
    </script>
    
